import 'package:flutter/material.dart';

import 'package:get/get.dart';
import 'package:illegalparking_app/controllers/sign_up_controller.dart';

import 'package:illegalparking_app/states/widgets/form.dart';

class SignUp extends StatefulWidget {
  const SignUp({super.key});

  @override
  State<SignUp> createState() => _SignUpState();
}

class _SignUpState extends State<SignUp> {
  final ScrollController _termsSummaryController = ScrollController();
  final ScrollController _termsController = ScrollController();
  final controller = Get.put(SignUpController());
  bool serviceTerms = false;

  @override
  void initState() {
    super.initState();
  }

  @override
  void dispose() {
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        centerTitle: true,
        title: const Text("회원가입"),
      ),
      body: Padding(
        padding: const EdgeInsets.all(8.0),
        child: Form(
          child: ListView(
            children: [
              // ID, PW
              createTextFormField(labelText: "아이디"),
              createTextFormField(labelText: "패스워드"),
              createTextFormField(labelText: "패스워드 확인"),
              // 이름,전화번호,인증
              createTextFormField(labelText: "이름"),
              createTextFormField(labelText: "전화번호"),
              createElevatedButton(text: "인증번호 발송"),
              createTextFormField(labelText: "인증번호"),
              createElevatedButton(text: "인증번호 확인"),
              // 약관
              SizedBox(
                height: 200,
                child: Padding(
                  padding: const EdgeInsets.all(8),
                  child: Scrollbar(
                    thumbVisibility: true,
                    controller: _termsSummaryController,
                    child: ListView.builder(
                      itemCount: 1,
                      controller: _termsSummaryController,
                      itemBuilder: (BuildContext context, int index) {
                        return const Text(
                            "");
                      },
                    ),
                  ),
                ),
              ),
              // 약관동의
              Padding(
                padding: const EdgeInsets.all(8.0),
                child: Row(
                  children: [
                    Checkbox(
                      value: serviceTerms,
                      onChanged: (bool? value) {
                        setState(() {
                          serviceTerms = value ?? false;
                        });
                      },
                    ),
                    const Text("약관 동의"),
                    const Spacer(),
                    TextButton(
                      onPressed: () {
                        showServiceTermsDialog();
                      },
                      child: const Text("더보기 >"),
                    ),
                  ],
                ),
              ),
              // 회원생성
              createElevatedButton(
                  text: "회원생성",
                  function: () {
                    showSignUpSuccessDialog();
                  })
            ],
          ),
        ),
      ),
    );
  }

  showServiceTermsDialog() {
    showDialog(
      context: context,
      builder: (BuildContext context) => Scaffold(
        appBar: AppBar(
          automaticallyImplyLeading: false,
          centerTitle: true,
          title: const Text("서비스 약관"),
          actions: [
            IconButton(
                onPressed: () {
                  Navigator.pop(context);
                },
                icon: const Icon(Icons.cancel_outlined))
          ],
        ),
        body: Padding(
          padding: const EdgeInsets.all(8),
          child: Column(
            children: [
              Expanded(
                child: Scrollbar(
                  thumbVisibility: true,
                  controller: _termsController,
                  child: ListView.builder(
                    itemCount: 1,
                    controller: _termsController,
                    itemBuilder: (BuildContext context, int index) {
                      return const Text(
                          "");
                    },
                  ),
                ),
              ),
              createElevatedButton(
                text: "약관동의하기",
                function: () {
                  Navigator.pop(context);
                },
              ),
            ],
          ),
        ),
      ),
    );
  }

  showSignUpSuccessDialog() {
    showDialog(
      context: context,
      builder: (BuildContext context) => Scaffold(
        appBar: AppBar(
          automaticallyImplyLeading: false,
          centerTitle: true,
          title: const Text("가입완료"),
          actions: [
            IconButton(
                onPressed: () {
                  Navigator.pop(context);
                },
                icon: const Icon(Icons.cancel_outlined))
          ],
        ),
        body: Padding(
          padding: const EdgeInsets.all(8),
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              const Padding(
                padding: EdgeInsets.all(8.0),
                child: Text("안녕하세요 OOO님\n회원가입이 완료되었습니다."),
              ),
              const Padding(
                padding: EdgeInsets.all(16.0),
                child: Center(child: Text("ID-000001")),
              ),
              Row(
                children: [
                  GetBuilder<SignUpController>(
                    builder: (controller) => Checkbox(
                      value: controller.checkedAutoLogin,
                      onChanged: (bool? value) {
                        controller.getAutoLogin(value ?? false);
                      },
                    ),
                  ),
                  const Text("자동로그인")
                ],
              ),
              createElevatedButton(
                text: "로그인하기",
                function: () {
                  Navigator.pop(context);
                  Navigator.pushNamed(context, "/login");
                },
              ),
            ],
          ),
        ),
      ),
    );
  }
}
